import os
import time
from plugins import gf

users_dir = os.path.join(r"users/")

def check_own_farm(own_farm):
    if own_farm == 1:
        return '‚†Ä‚†Äüîã –§–µ—Ä–º–∞: Miner\n'
    elif own_farm == 2:
        return '‚†Ä‚†Äüîã –§–µ—Ä–º–∞: Miner S\n'
    elif own_farm == 3:
        return '‚†Ä‚†Äüîã –§–µ—Ä–º–∞: Miner X\n'
    else:
        pass

